import React, { useState, useEffect } from 'react';

export const TablaComp = ({ cap, datos }) => {
  const [dataHeaders, setDataHeaders] = useState([]);
  const [dataRows, setDataRows] = useState([]);

  useEffect(() => {
    updateConfigRows();
  }, [cap]);

  useEffect(() => {
    updateDataRows();
  }, [datos]);

  const updateConfigRows = () => {
    if (!cap) {
      return;
    }

    const headers = cap.map((header, index) => (
      <th key={index}>{header.cap} </th>
    ));
    setDataHeaders(headers);
  };

  const updateDataRows = () => {
    if (!datos) {
      return;
    }

    const rows = datos.map((dataObject, dataIndex) => (
      <tr key={dataIndex}>
        {cap.map((header, configIndex) => (
          <td key={configIndex}>{dataObject[header.camp]}</td>
        ))}
      </tr>
    ));
    setDataRows(rows);
  };

  return (
    <table>
      <thead>
        <tr>
          {dataHeaders}
        </tr>
      </thead>
      <tbody>
        {dataRows}
      </tbody>
    </table>
  );
};
