import { Rllista } from './Rllista';
import { useState } from 'react';



export function Container() {
  // Desestructuració d'arrays
  let [titol, setTitol] = useState("Titol Llista");
  let [datos, setDatos] = useState(["John Doe", "Pepe", "manel"]);

  function removeElem() {
    datos.splice(0, 1);
    setDatos([...datos]);
  }

  function addElem() {
    let elem = prompt("Nou item de la llista");
    if (elem) {
      datos.push(elem);
      setDatos([...datos]);
    }
  }

  function canviTitol() {
    let tt = prompt("Nou titol:")
    if (tt) {
      setTitol(tt);
    //document.getElementById("titol").innerHTML = tt;
  }
  class Pokemon {
    constructor(nombre, tipo, nivel, salud, ataque) {
      this._nombre = nombre;
      this._tipo = tipo;
      this._nivel = nivel;
      this._salud = salud;
      this._ataque = ataque;
    }
  }
  }

  return (
    <>
      <h1 onClick={canviTitol}>{titol}</h1>
      <div>
        <Rllista  data={datos} />
        <button  onClick={addElem}>Afegir</button>
        <button  onClick={(removeElem)}>Eliminar</button>
      </div>
    </>
  );
}
