import { Rllista } from "./Rllista";
import { useState } from "react";

export function Container() {
  // Desestructuració d'arrays
  let [titol, setTitol] = useState("Titol Llista");
  let [datos, setDatos] = useState(["John Doe", "Pepe", "manel"]);
  let [llista, setLlista] = useState([]);
  function removeElem() {
    datos.splice(0, 1);
    setDatos([...datos]);
  }

  function addElem() {
    let elem = prompt("Nou item de la llista");
    if (elem) {
      datos.push(elem);
      setDatos([...datos]);
    }
  }

  function canviTitol() {
    let tt = prompt("Nou titol:");
    if (tt) {
      setTitol(tt);
      //document.getElementById("titol").innerHTML = tt;
    }
    class Pokemon {
      constructor(nombre, tipo, nivel, salud, ataque) {
        this._nombre = nombre;
        this._tipo = tipo;
        this._nivel = nivel;
        this._salud = salud;
        this._ataque = ataque;
      }
      // Métodos get
      get nombre() {
        return this._nombre;
      }

      get tipo() {
        return this._tipo;
      }

      get nivel() {
        return this._nivel;
      }

      get salud() {
        return this._salud;
      }

      get ataque() {
        return this._ataque;
      }

      // Métodos set
      set nombre(nuevoNombre) {
        this._nombre = nuevoNombre;
      }

      set tipo(nuevoTipo) {
        this._tipo = nuevoTipo;
      }

      set nivel(nuevoNivel) {
        this._nivel = nuevoNivel;
      }

      set salud(nuevaSalud) {
        this._salud = nuevaSalud;
      }

      set ataque(nuevoAtaque) {
        this._ataque = nuevoAtaque;
      }
    }
    class PokemonLegendario extends Pokemon {
      constructor(nombre, tipo, nivel, salud, ataque, poder) {
        super(nombre, tipo, nivel, salud, ataque);
        this.poder = poder;
      }
      // Métodos get para el atributo de la clase hija
      get poder() {
        return this._poder;
      }

      // Método set para el atributo de la clase hija
      set poder(nuevoPoder) {
        this._poder = nuevoPoder;
      }
    }
    class PokemonNormal extends Pokemon {
      constructor(nombre, tipo, nivel, salud, ataque, habilidad) {
        super(nombre, tipo, nivel, salud, ataque);
        this.habilidad = habilidad;
      }
      get habilidad() {
        return this._habilidad;
      }
      // Método set para el atributo de la clase hija
      set habilidad(nuevaHabilidad) {
        this._habilidad = nuevaHabilidad;
      }
    }
    const crearPokemon = () => {
      const pokemon1 = new Pokemon("Pikachu", "Eléctrico", 30, 100, 50);
      const pokemon2 = new Pokemon("Charmander", "Fuego", 30, 100, 50);

      const nuevaLista = [...llista, pokemon1, pokemon2];
      setLlista(nuevaLista);
    };

    const crearPokemonLegendario = () => {
      const pokemon3 = new PokemonLegendario(
        "Mewtwo",
        "Psíquico",
        80,
        150,
        80,
        "Telequinesis"
      );

      const nuevaLista = [...llista, pokemon3];
      setLlista(nuevaLista);
    };

    const crearPokemonNormal = () => {
      const pokemon5 = new PokemonNormal(
        "Pikachu",
        "Normal",
        10,
        100,
        20,
        "ElectricPower"
      );
      const pokemon6 = new PokemonNormal(
        "Eevee",
        "Normal",
        12,
        120,
        25,
        "Adaptability"
      );
      const pokemon7 = new PokemonNormal("Meowth", "Normal", 8, 80, 18);
      const pokemon8 = new PokemonNormal(
        "Snorlax",
        "Normal",
        15,
        150,
        30,
        "Thick Fat"
      );

      const nuevaLista = [...llista, pokemon5, pokemon6, pokemon7, pokemon8];
      setLlista(nuevaLista);
    };
  }

  return (
    <div>
      <button onClick={crearPokemon}>Crear Pokémon</button>
      <button onClick={crearPokemonLegendario}>Crear Pokémon Legendario</button>
      <button onClick={crearPokemonNormal}>Crear Pokémon Normal</button>

      <div id="resultado">
        {llista.length > 0 && (
          <div>
            <h2>Lista de Pokémon:</h2>
            {llista.map((pokemon, index) => (
              <div key={index}>{JSON.stringify(pokemon)}</div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
