import React, { useState, useEffect } from 'react';

export const Table = ({ configArray, dataArray }) => {
  const [dataHeaders, setDataHeaders] = useState([]);
  const [dataRows, setDataRows] = useState([]);

  useEffect(() => {
    updateConfigRows();
  }, [configArray]);

  useEffect(() => {
    updateDataRows();
  }, [dataArray]);

  const updateConfigRows = () => {
    if (!configArray) {
      return;
    }

    const headers = configArray.map((configItem, index) => (
      <th key={index}>{configItem.cap} </th>
    ));
    setDataHeaders(headers);
  };

  const updateDataRows = () => {
    if (!dataArray) {
      return;
    }

    const rows = dataArray.map((dataObject, dataIndex) => (
      <tr key={dataIndex}>
        {configArray.map((configItem, configIndex) => (
          <td key={configIndex}>{dataObject[configItem.camp]}</td>
        ))}
      </tr>
    ));
    setDataRows(rows);
  };

  return (
    <table>
      <thead>
        <tr>
          {dataHeaders}
        </tr>
      </thead>
      <tbody>
        {dataRows}
      </tbody>
    </table>
  );
};
