import { getDataArrays } from '../services/arrays';
import { TablaComp } from './Rllista';
import { useState } from 'react';



export function Container() {
  // Desestructuració d'arrays
const {arrayClase, arraySubclase,arraySubclase2,configList} = getDataArrays();

  let [titol, setTitol] = useState("*POKEDEX*");
  let [datos, setDatos] = useState([]);
  let [datosOriginal, setDatosOriginal] = useState([]);
  let [cap, setCap] = useState([]);
  let [totalizar, setTotalizar] = useState([]);
  let [mostrarTotalizar, setmostrarTotalizar] = useState([]);
  const tableRef = useRef(null);

  function removeElem() {
    datos.splice(0, 1);
    setDatos([...datos]);
  }
function renderTaula(){
  setCap(configList)
}
function renderClase(){
  let newData=[...datos];
  newData.push(...arrayClase);
 setDatos(newData)
}
function renderSlegendario(){
  let newData=[...datos];
  newData.push(...arraySubclase);
 setDatos(newData)
}
function renderSnormal(){
  let newData=[...datos];
  newData.push(...arraySubclase2);
 setDatos(newData)
}
function mostrarTotal() {
  if (mostrarTotalizar) {
    setmostrarTotalizar(false);
    const total = datos.reduce(totalizar, 0);
    setTotalizar("La suma total del camp és: " + total);

    function totalizar(total, num) {
      let element = "ataque";
      if (isNaN(total + num[element]) || num[element] === null) {
        return total;
      } else {
        return total + num[element];
      }
    }
  } else {
    setmostrarTotalizar(true);
    setTotalizar(null);
  }
}

function ordenarPokemon() {
  let elemento = "nivel";

  // Accede al elemento de la tabla utilizando la referencia (ref)
  const tableComponent = tableRef.current;

  // Verifica si la referencia y el componente existen antes de intentar acceder al elemento HTML
  if (tableRef.current && tableComponent) {
    const tableElement = tableComponent.querySelector("table");

    if (tableElement) {
      // Lógica de ordenación aquí
      // Por ejemplo:
      const filas = Array.from(tableElement.getElementsByTagName('tr'));
      filas.sort((a, b) => {
        const valorA = parseInt(a.getAttribute('data-nivel'));
        const valorB = parseInt(b.getAttribute('data-nivel'));
        return valorB - valorA;
      });

      // Vuelve a agregar las filas ordenadas a la tabla
      filas.forEach(fila => tableElement.appendChild(fila));
    }
  }
}



  function canviTitol() {
    let tt = prompt("Nou titol:")
    if (tt) {
      setTitol(tt);
    //document.getElementById("titol").innerHTML = tt;
  }
  }

  return (
    <>
      <h1 >{titol}</h1>
      <div>

        <button  onClick={canviTitol}>Modificar Titol</button>
        <button  onClick={renderTaula}>Capçaleres</button>
        <button  onClick={(renderClase)}>Pokemons</button>
        <button  onClick={(renderSlegendario)}>Pokemon Legendario</button>
        <button  onClick={(renderSnormal)}>Pokemon Normal</button>
        <button  onClick={(mostrarTotal)}>Totalizar</button>
        <button  onClick={(ordenarPokemon)}>Ordenar</button>
        <button  onClick={(removeElem)}>Filtrar</button>
        <button  onClick={(removeElem)}>Volver Original</button>
        <TablaComp  datos={datos} cap={cap}  />
        <div class="totalizar">{totalizar} </div>
      </div>
    </>
  );
}
