import { TablaComp } from './Rllista';
import { useState } from 'react';



export function Container() {
  // Desestructuració d'arrays
  let [titol, setTitol] = useState("Titol Llista");
  let [datos, setDatos] = useState([]);
  let [cap, setCap] = useState([]);

  function removeElem() {
    datos.splice(0, 1);
    setDatos([...datos]);
  }

  function addElem() {
    if (elem) {
      datos.push(elem);
      setDatos([...datos]);
    }
  }


  function canviTitol() {
    let tt = prompt("Nou titol:")
    if (tt) {
      setTitol(tt);
    //document.getElementById("titol").innerHTML = tt;
  }

  }

  return (
    <>
      <h1 >{titol}</h1>
      <div>
        <TablaComp  data={datos} capsalera={cap}  />
        <button  onClick={canviTitol}>Modificar Titol</button>
        <button  onClick={addElem}>Capçaleres</button>
        <button  onClick={(removeElem)}>Pokemons</button>
        <button  onClick={(removeElem)}>Pokemon Legendario</button>
        <button  onClick={(removeElem)}>Pokemon Normal</button>
        <button  onClick={(removeElem)}>Totalizar</button>
        <button  onClick={(removeElem)}>Ordenar</button>
        <button  onClick={(removeElem)}>Volver Original</button>
      </div>
    </>
  );
}
